	//---------------------------------------------------------
	//this access method throws an error if someone tries to access a non existent crystal.
	xb_cry &xb_ball::at( const unsigned int i ){
		if( i > 162 && i < 1 ) throw XB::error( "Out of range!", "xb_ball::at()" );
		else return ball[i-1];
	}

	//---------------------------------------------------------
	//this function returns the list of the indexes of the neighbours of
	//up to the order "order" for a given crystal.
	//WARNING: this function has complexiti O( exp(n) )! Use with caution!
	//NOTE: this is essentially a tree search, with the three branching factor 6
	//      normally, one should consider doing this in parallel and not recursively
	//      since we are in >2016. But since we have only actually 162 crystals
	//      it makes no sense. Besides, one should NOT look for high order neighbours
	unsigned int* neigh( xb_cry &cry, unsigned int order, unsigned int &length ){
		unsigned int *neigh_list; //here the list will be saved
		unsigned int *buf; //a buffer for storing temporary data
		unsigned int len[6]; //the lengths 
	
		//allocate at least enough memory
		neigh_list = (unsigned int*)malloc( (pow(6,order)+1)*sizeof(unsigned int) );
		//and check if it went badly
		if( neigh_list == NULL ) throw XB::error( "Memory error!", "neigh" );
	
		//set it to 0
		memset( neigh_list, 0, pow(6,order)*sizeof(unsigned int) );
	
		//if order is more than 1, call the function recursively
		if( order > 1 ){		
			//for each of the neighbour to the current crystal (readily accessible)
			//call this function to one order less.
			for( int c=0; c < ( cry.sh == PENTAGON ? 5 : 6 ); ++c ){
				buf = neigh( *cry.neigh[c], order-1, len[c] ); //get the neighbour list for the order-1
				memcpy( &neigh_list[(int)pow(6,order-1)*c], buf, len[c]*sizeof(unsigned int) ); //cat it into
				                                                                                //the buffer
				free( buf ); //free the buffer
			}
		} else { //if the order is one, just look up the neighbours.
			//direct neighbour resolution
			for( int c=0; c < ( cry.sh == PENTAGON ? 5 : 6 ); ++c )
				neigh_list[c] = cry.neigh[c]->i;
			neigh_list[( cry.sh == PENTAGON ? 5 : 6 )] = cry.i; //this is for consistency if the order request
			                                                    //is directly 1. Otherwise I always return
			                                                    //the calling crystal except in the case of
			                                                    //direct resolution.
		}
	
		//prune the neigh_list
		std::sort( neigh_list, neigh_list+(int)pow(6,order)+1 ); //sorting is a necessary step
		unsigned int* last = std::unique( neigh_list, neigh_list+(int)pow(6,order)+1 ); //just preserve the
		                                                                               //unique entries
			                                                                             //and get the
		                                                                               //terminating pointer
		length = (unsigned int)(last - neigh_list); //set the length of the produced list (will bubble
		                                            //out of the function!)
		neigh_list = (unsigned int*)realloc( neigh_list, length*sizeof(unsigned int) ); //shrink the list
		                                                                                //to fit its length
		if( neigh_list == NULL ) throw XB::error( "Memory error!", "neigh" );
	
		//return it
		return neigh_list;
	}

